import React, { useEffect, useState } from "react";
import axios from "axios";

export default function FloorPlan() {
  const [units, setUnits] = useState({});
  const [selectedUnit, setSelectedUnit] = useState(null);

  useEffect(() => {
    axios.get("/api/office_units").then((res) => {
      const mapped = {};
      res.data.forEach((u) => {
        mapped[u.identifier] = u;
      });
      setUnits(mapped);
    });
  }, []);

  const getFillColor = (id) => {
    const unit = units[id];
    if (!unit) return "#ccc";
    return unit.status === "occupied" ? "#ff6961" : "#77dd77";
  };

  const handleClick = (id) => {
    setSelectedUnit(units[id]);
  };

  return (
    <div style={{ display: "flex" }}>
      <svg
        viewBox="0 0 800 600"
        style={{ width: "60%", height: "500px", border: "1px solid #ddd" }}
      >
        <rect
          id="office-a"
          x="50"
          y="50"
          width="100"
          height="100"
          fill={getFillColor("office-a")}
          onClick={() => handleClick("office-a")}
          style={{ cursor: "pointer" }}
        />
        <rect
          id="office-b"
          x="200"
          y="50"
          width="100"
          height="100"
          fill={getFillColor("office-b")}
          onClick={() => handleClick("office-b")}
          style={{ cursor: "pointer" }}
        />
      </svg>

      <div style={{ marginLeft: "20px" }}>
        {selectedUnit ? (
          <div>
            <h3>{selectedUnit.name}</h3>
            <p>Status: {selectedUnit.status}</p>
            <p>Occupant: {selectedUnit.occupant || "None"}</p>
            <p>Move-in Date: {selectedUnit.move_in_date}</p>
          </div>
        ) : (
          <p>Click a room to view details</p>
        )}
      </div>
    </div>
  );
}
