import React, { useEffect, useState, useRef } from "react";
import axios from "axios";

const roomIds = ["office-a", "office-b"];

export default function FloorPlan() {
  const [units, setUnits] = useState({});
  const [selectedUnit, setSelectedUnit] = useState(null);
  const svgRef = useRef(null);

  useEffect(() => {
    axios.get("/api/office_units").then((res) => {
      const mapped = {};
      res.data.forEach((u) => {
        mapped[u.identifier] = u;
      });
      setUnits(mapped);
    });
  }, []);

  useEffect(() => {
    const svgDoc = svgRef.current?.contentDocument;
    if (!svgDoc) return;

    roomIds.forEach((id) => {
      const el = svgDoc.getElementById(id);
      if (el) {
        el.style.cursor = "pointer";
        el.addEventListener("click", () => setSelectedUnit(units[id]));
        el.setAttribute("fill", units[id]?.status === "occupied" ? "#ff6961" : "#77dd77");
      }
    });
  }, [units]);

  return (
    <div style={{ display: "flex" }}>
      <object
        type="image/svg+xml"
        data="/floorplan.png"
        ref={svgRef}
        style={{ width: "60%", height: "500px" }}
      />
      <div style={{ marginLeft: "20px" }}>
        {selectedUnit ? (
          <div>
            <h3>{selectedUnit.name}</h3>
            <p>Status: {selectedUnit.status}</p>
            <p>Occupant: {selectedUnit.occupant || "None"}</p>
            <p>Move-in Date: {selectedUnit.move_in_date}</p>
          </div>
        ) : (
          <p>Click a room to view details!</p>
        )}
      </div>
    </div>
  );
}
